mapper.insert:
INSERT INTO LIKE_STORES (
    LS_IDX,
    STORE_IDX,
    USER_IDX
) VALUES (
    LIKE_STORES_SEQ.NEXTVAL,
    #{store_idx},
    #{user_idx}
)

mapper.select:
    SELECT COUNT(LS_IDX)
	FROM LIKE_STORES
	WHERE  STORE_IDX = #{store_idx}

    SELECT LS_IDX
	FROM LIKE_STORES
	WHERE  STORE_IDX = #{store_idx} AND user_idX = #{user_idx}

    SELECT  COUNT(LR_IDX)
	FROM  LIKE_REVIEWS
	WHERE  REVIEW_IDX = #{review_idx}

    SELECT LR_IDX
	FROM LIKE_REVIEWS
	WHERE  REVIEW_IDX = #{review_idx} AND user_idX = #{user_idx}

    SELECT  COUNT(LR_IDX)
	FROM  LIKE_REVIEWS
	WHERE  REVIEW_IDX = #{review_idx}

SELECT BOOKMARK_IDX
FROM BOOKMARKS
WHERE STORE_IDX = #{store_idx} AND user_idX = #{user_idx}

mapper.insert:
 INSERT INTO BOOKMARKS (
    BOOKMARK_IDX,
    USER_IDX,
    STORE_IDX
) VALUES (
    BOOKMARKS_SEQ.NEXTVAL,
    #{user_idx},
    #{store_idx}
)
